class Game:
    
    def __init__(self,master):

        master.title('Wildcard Guess Game')
        master.iconbitmap(r'')                                                      # put icon
        global qNo
        qNo = 1

        style = ttk.Style(mainWin)
        style.configure("TButton", font=('wasy10', 20), padding=10)

        menu = Menu(master)
        master.config(menu=menu)

        file = Menu(menu, tearoff=0)
        file.add_command(label='Restart', command=lambda:self.fillFrame(1))
        file.add_separator()
        file.add_command(label='Quit', command=master.destroy)
        menu.add_cascade(label='File', menu=file)

        self.mainFrame = Frame(master, width=500, height=500)
        self.mainFrame.grid(row=0,column=0)

        self.lSpace = Label(self.mainFrame, width=12, height=10)
        self.lSpace.grid(row=0, column=0)

        self.photo = PhotoImage(data=start) 
        self.start = ttk.Button(self.mainFrame, command=lambda:[self.clearFrame(),self.fillFrame(qNo)], image=self.photo)
        self.start.grid(row=1, column=1)

    # ***** Functions *****

    def fillFrame(self,Question):
        global randNum
        randNum = random.randint(1,3)
        if randNum == 1:
            img = PhotoImage(data=questions[Question-1][randNum-1])
        elif randNum == 2:
            img = PhotoImage(data=questions[Question-1][randNum-1])
        else:
            img = PhotoImage(data=questions[Question-1][randNum-1])
        
        self.image = Label(self.mainFrame, image=img)
        self.image.image = img
        self.image.grid(row=1, column=1, columnspan=2, sticky=W+E+N+S, pady=10)

        self.answer = ttk.Entry(self.mainFrame, font='Helvetica 15', justify=LEFT)
        self.answer.grid(row=2, column=1, columnspan=2, sticky=W+E+N+S, pady=10)

        self.submit = ttk.Button(self.mainFrame, text='Submit', style='TButton', command=lambda:self.Submit(Question))
        self.submit.grid(row=3, column=1, columnspan=2, pady=10)

        self.lSpace = Label(self.mainFrame, width=12, height=7)
        self.lSpace.grid(row=0, column=0)

        self.rSpace = Label(self.mainFrame, width=10, height=10)
        self.rSpace.grid(row=4, column=3)

    def Submit(self,Question):
        answer = self.answer.get().lower()
        if answer in answers[Question-1][randNum-1]:
            print('correct answer')
            global qNo
            qNo += 1
            self.clearFrame()
            self.fillFrame(qNo)
        else:
            print('wrong')
                

    def clearFrame(self):
        for widget in self.mainFrame.winfo_children():
            widget.grid_remove()



mainWin = Tk()
mainWin.resizable(False,False)
mainWin.maxsize(500,500)
mainWin.minsize(500,500)
app = Game(mainWin)
mainWin.mainloop()

